function Comment(){this.submitBtn=$(".submit-buttom")}Comment.prototype.listenContenUpload=function(){var o=$("#comment");this.submitBtn.click(function(){var n=o.val(),t=o.data("news-id");myajax.post({url:"/news/comment/",data:{news_id:t,content:n},success:function(n){if(200===n.code)window.location.reload();else{var t=n.message;if("string"==typeof t||t.constructor==String)window.messageBox.showError(t);else for(var o in t){var e=t[o][0];window.messageBox.showError(e)}}}})})},Comment.prototype.run=function(){this.listenContenUpload()},$(function(){(new Comment).run()});